import React, { useState, useEffect } from 'react'
import { Container } from '@material-ui/core';
import Grid from '@material-ui/core/Grid';
import experience from '../components/Data/experienceData'
import { FaAngleDoubleRight } from 'react-icons/fa'
import '../components/About.css'
function About() {
    const [jobs, setJobs] = useState(experience);
    const [value, setValue] = useState(0)

    console.log(jobs)


    const { company, dates, duties, title } = jobs[value];
    return (
        <Container>
            <Grid container spacing={2}>

                <Grid item xs={12} lg={4} >
                    <div style={{ textAlign: 'center' }}>
                        <img
                            src="../../public/item-2.jpeg"
                            alt="avatar"
                            style={{ height: '200px' }}
                        />
                        <h2 style={{ paddingTop: '2em' }}>Paul Hanna</h2>
                        <h4 style={{ color: 'grey' }}>Programmer</h4>
                        <hr style={{ borderTop: '3px solid #833fb2', width: '50%' }} />
                        <p>Lorem Ipsum is simply dummy text of the printing and typesetting industry. Lorem Ipsum has been the industry's standard dummy text ever since the 1500s, when an unknown printer took a galley of type and scrambled it to make a type specimen book. It has survived not only five centuries, but also the leap into electronic typesetting, remaining essentially unchanged.</p>
                        <hr style={{ borderTop: '3px solid #833fb2', width: '50%' }} />
                        <h5>Address</h5>
                        <p>1 Hacker Way Menlo Park, 94025</p>
                        <h5>Phone</h5>
                        <p>(123) 456-7890</p>
                        <h5>Email</h5>
                        <p>someone@example.com</p>
                        <h5>Web</h5>
                        <p>mywebsite.com</p>
                        <hr style={{ borderTop: '3px solid #833fb2', width: '50%' }} />
                    </div>
                </Grid>
                <Grid item xs={12} lg={8} >
                    <section className='section'>
                        <div className='title'>
                            <h2>experience</h2>
                            <div className='underline'></div>
                        </div>
                        <div className='jobs-center'>


                            <div className='btn-container'>
                                {
                                    jobs.map((item, index) => {

                                        return (
                                            <button
                                                key={item.id}
                                                onClick={() => setValue(index)}
                                                // if the index of the button matches the current setValue,then add active-btn property
                                                className={`job-btn ${index === value && 'active-btn'}`}
                                            >
                                                {item.company}
                                            </button>
                                        )
                                    })
                                }
                            </div>

                            <article className='job-info'>
                                <h3>{title}</h3>
                                <h4>{company}</h4>
                                <p className='job-date'>{dates}</p>

                                {duties.map((duty, id) => {


                                    return (<div key={id} className='job-desc'>
                                        <FaAngleDoubleRight className='job-icon'></FaAngleDoubleRight>
                                        <p>{duty}</p>
                                    </div>
                                    )
                                })}
                            </article>
                        </div>
                    </section>
                </Grid>
            </Grid>
        </Container>
    )
}

export default About
