      <header>
        <h4>{title}</h4>
        <button className='btn' onClick={() => setShowInfo(!showInfo)}>
          {showInfo ? <AiOutlineMinus /> : <AiOutlinePlus />}
        </button>
      </header>
      {/* if show info is true,show info */}
      {showInfo && <p>{info}</p>}
    </article>
  )


};

export default Question;
